const form=document.querySelector("form#playlist-form"),loading=document.querySelector("#loading"),content=document.querySelector("#content"),baseUrl="https://playlistyt.herokuapp.com/playlist";function durationToWords(o){let t="";return o.hours&&(t+=`${o.hours} hours `),o.minutes&&(t+=`${o.minutes} minutes `),o.seconds&&(t+=`${o.seconds} seconds `),t}async function getPlaylistInfo(o){o.preventDefault(),content.innerHTML="",toggleLoading();const t=await fetch(baseUrl,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({playlistId:form.playlistid.value})}),e=await t.json();let n=`<b>Total videos</b>: ${e.videoCount}<br><b>Total duration of all videos</b>: ${durationToWords(e.totalDuration)}<br><b>Average duration of a video</b>: ${durationToWords(e.averageDuration)}<br>`;for(let o of Object.keys(e.speed))n+=`<b>At a speed ${o} :</b> ${durationToWords(e.speed[o])}<br>`;content.innerHTML=n,toggleLoading()}function toggleLoading(){loading.classList.toggle("hidden")}form.addEventListener("submit",getPlaylistInfo);